

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = (byte)19898;
		short b = 20;
		int c = 30;
		long d = 40;
		System.out.println(a);
		if(true){
			System.out.println("Inside if");
		}
		char ch = 'a';
		System.out.println((int)ch);
		
		float x = 10.0F;
		double y = 20.0;
	}

}
